mapper.select:
select 

,

from b2c_promotion
where PROMOTION_ID = #{promotionId,jdbcType=VARCHAR}
  and PROMOTION_STATUS = #{promotionStatus,jdbcType=CHAR}
  and PROMOTION_START_TIME = #{promotionStartTime,jdbcType=TIMESTAMP}
  and PROMOTION_END_TIME = #{promotionEndTime,jdbcType=TIMESTAMP}

mapper.delete:
delete from b2c_promotion
where PROMOTION_ID = #{promotionId,jdbcType=VARCHAR}
  and PROMOTION_STATUS = #{promotionStatus,jdbcType=CHAR}
  and PROMOTION_START_TIME = #{promotionStartTime,jdbcType=TIMESTAMP}
  and PROMOTION_END_TIME = #{promotionEndTime,jdbcType=TIMESTAMP}
