mapper.select:
select 

from b2c_promotion_param_value
where PROMOTION_ID = #{promotionId,jdbcType=VARCHAR}
  and PROMOTION_PARAM_ID = #{promotionParamId,jdbcType=VARCHAR}

mapper.delete:
delete from b2c_promotion_param_value
where PROMOTION_ID = #{promotionId,jdbcType=VARCHAR}
  and PROMOTION_PARAM_ID = #{promotionParamId,jdbcType=VARCHAR}

mapper.update:
update b2c_promotion_param_value
set PROMOTION_PARAM_NAME = #{promotionParamName,jdbcType=VARCHAR},
  PROMOTION_PARAM_VALUE = #{promotionParamValue,jdbcType=VARCHAR},
  PROMOTION_PARAM_DNAME = #{promotionParamDname,jdbcType=VARCHAR},
  PROMOTION_PARAM_TYPE = #{promotionParamType,jdbcType=VARCHAR}
where PROMOTION_ID = #{promotionId,jdbcType=VARCHAR}
  and PROMOTION_PARAM_ID = #{promotionParamId,jdbcType=VARCHAR}
